    /**
     * 随机生成一个姓名 姓1位 名1-2位
     * @return 姓名（2-3个字）
     */
    public static String generateName(){
        Random random = new Random();
        int size = random.nextInt(2) + 1;
        String name = "";
        name += NAME_PREFIX[random.nextInt(NAME_PREFIX.length)];
        name += NAME_SUFFIX[random.nextInt(NAME_SUFFIX.length)];
        if (size > 1) {
            name += NAME_SUFFIX[random.nextInt(NAME_SUFFIX.length)];
        }
        return name;
    }
